INSERT INTO `snippets` VALUES (1,'main-navigation','','<style type=\"text/css\">\r\n  #mainmenu { float: right; }\r\n  .button { width: 130px; padding: 0px; text-align: center; z-index: 999; }\r\n  .button a.here { color: #A6D22E; }\r\n  .submenu { display: none; }\r\n  .sm { padding-top: 2px; }\r\n  .sm.selected { background-image: url(/images/bg_sm-over.png); }\r\n</style>\r\n\r\n<div id=\"menu_container\">\r\n  <div id=\"mainmenu\">\r\n    <r:find url=\"/\">\r\n      <r:children:each offset=\"1\">\r\n        <r:if_content part=\"session\">\r\n          <div class=\"button\">\r\n      	    <a href=\"<r:url />\"><r:title /></a>\r\n      	    <div class=\"submenu\">\r\n              <r:children:each offset=\"1\">\r\n                <div class=\"sm\"><a href=\"<r:url />\"><r:title /></a></div>\r\n              </r:children:each>\r\n      	    </div>\r\n          </div>\r\n        </r:if_content>\r\n      </r:children:each>\r\n    </r:find>\r\n  </div>\r\n  <div style=\"clear:both;\"></div>\r\n</div>\r\n<div style=\"clear:both;\"></div>\r\n\r\n<script type=\"text/javascript\">\r\n<!--\r\njQuery(function ($) {\r\n\r\n  function selectSubmenuButton() {\r\n    var parts = \'<r:url />\'.split(\'/\');\r\n    if (parts.length === 3) {\r\n      $(\'.button a[href*=\'+parts[1]+\']:first\').addClass(\'here\');\r\n      return;\r\n    }\r\n    var url = parts[1] + \'/\' + parts[2];\r\n    var smb = $(\'.sm a[href*=\'+url+\']\');\r\n    smb.parents(\'.sm\').addClass(\'selected\');\r\n    smb.parents(\'.submenu\').show();\r\n  }\r\n\r\n  var subMenus = $(\"#mainmenu .submenu\");\r\n  var mainButtons = $(\"#mainmenu > .button > a\");\r\n\r\n  subMenus.hide();\r\n  selectSubmenuButton();\r\n\r\n  mainButtons.mouseover( function () {\r\n    subMenus.hide();\r\n    $(this).next(\'.submenu\').show();\r\n  });  \r\n\r\n});\r\n//-->\r\n</script>\r\n\r\n','2009-05-23 01:07:15','2009-05-26 04:21:20',1,1,50),(2,'footer','','<div id=\"footer\">\r\n<div id=\"footermenu\">\r\n<div id=\"footertxt\">\r\n  <a href=\"/\">Home</a> | \r\n  <a href=\"/profile/biography/\">Profile</a> | \r\n  <a href=\"/services/services-offered/site-assessments-workshops/\">Services</a> | \r\n  <a href=\"/cooke-report/\">Cooke Report</a> | \r\n  <a href=\"/events/events-calendar/\">Events</a> | \r\n  <a href=\"/media-room/in-the-news/\">Media Room</a> | \r\n  <a href=\"/contact/\">Contact Us</a> | \r\n  <a href=\"/site-map/\">Site Map</a> | \r\n  <a href=\"/privacy-policy/\">Privacy Policy</a>\r\n  <br/><br/>\r\n  <span class=\"copyright\">\r\n    Â© 2009 All Rights Reserved.\r\n    <a href=\"/\" style=\"font-size:10px;\">GordCooke.com</a>\r\n  </span>\r\n</div>\r\n</div>\r\n</div>','2009-05-23 01:11:43','2009-05-24 01:20:36',1,1,4),(3,'quick-navigation','','<div id=\"inner_col-left\"> \r\n  <a href=\"/book-gord/\"><img src=\"/images/left-col/icon_book-gord.gif\" alt=\"Book Gord\" width=\"156\" height=\"83\" style=\"margin-top:5px;\" /></a>\r\n  <a href=\"/meet-gord/\"><img src=\"/images/left-col/icon_meet-gord.gif\" alt=\"Meet Gord\" width=\"156\" height=\"83\" style=\"margin-top:5px;\" /></a>\r\n  <a href=\"/read-gord/\"><img src=\"/images/left-col/icon_read-gord.gif\" alt=\"Read Gord\" width=\"156\" height=\"83\" style=\"margin-top:5px;\" /></a>\r\n  <a href=\"/media-room/articles-publications/\"><img src=\"/images/left-col/icon_whatsup-gord.gif\" alt=\"What\'s Up\" width=\"156\" height=\"83\" style=\"margin-top:5px;\" /></a> \r\n</div>','2009-05-23 01:39:35','2009-05-24 00:55:44',1,1,5),(6,'sub-navigation-children','','<div id=\"inner_col-left\">\r\n  <div id=\"subsubmenu\">\r\n    <ul>\r\n    <r:children:each offset=\"1\">\r\n      <li>\r\n         <a href=\"<r:url />\" style=\"text-transform: uppercase;\">\r\n           <r:title />\r\n         </a>\r\n      </li>\r\n    </r:children:each>\r\n    </ul>\r\n  </div>\r\n</div>','2009-05-23 06:32:49','2009-05-23 09:21:09',1,1,5),(7,'sub-navigation-sibling','','<div id=\"inner_col-left\">\r\n  <div id=\"subsubmenu\">\r\n    <ul>\r\n    <r:parent>\r\n    <r:children:each offset=\"1\">\r\n      <li>\r\n         <a href=\"<r:url />\" style=\"text-transform: uppercase;\">\r\n           <r:if_self><strong></r:if_self>\r\n             <r:title />\r\n           <r:if_self></strong></r:if_self>\r\n         </a>\r\n      </li>\r\n    </r:children:each>\r\n    </r:parent>\r\n    </ul>\r\n  </div>\r\n</div>','2009-05-23 06:41:20','2009-05-23 09:20:46',1,1,3),(8,'site-map','',' <r:children:each by=\"title\" order=\"asc\">\r\n   <r:unless_content part=\"no-map\">\r\n     <li>\r\n       <r:link />\r\n       <r:if_children>\r\n         <ul class=\"second\">\r\n           <r:snippet name=\"site-map\" />\r\n         </ul>\r\n       </r:if_children>\r\n     </li>\r\n   </r:unless_content>\r\n </r:children:each>\r\n\r\n','2009-05-26 04:14:24','2009-05-26 04:14:24',1,NULL,0);
