INSERT INTO `snippets` VALUES (1,'main-navigation','','<style type=\"text/css\">\r\n/* SUB MENU CONTAINER */\r\n#mainmenu { float: right; }\r\n.button { width: 130px; padding: 0px; text-align: center; z-index: 999; }\r\n.button a.here { color: #A6D22E; }\r\n.submenu {position:absolute;width:150px; z-index: 999; display:none; }\r\n.subsubmenu {position:absolute;width:260px; z-index: 999; margin-left:152px; display:none; }\r\n.sm.selected { background-image: url(/images/bg_sm-over.png); }\r\n\r\n/* SUB MENU */\r\n.sm {background-image:url(/images/bg_sm.png);font-size:12px;margin-top:1px;margin-bottom:1px;height:15px;padding-top:1px;padding-bottom:1px;}\r\n.sm:hover {background-image:url(/images/bg_sm-over.png);}\r\n.sm a {font-size:12px;color:#fff;text-transform:none; font-weight:normal;} \r\n.sm a:hover {font-size:12px;color:#fff; text-decoration:none; font-weight:bold;}  \r\n</style>\r\n\r\n<div id=\"menu_container\">\r\n  <div id=\"mainmenu\">\r\n    <r:find url=\"/\">\r\n      <r:children:each offset=\"1\">\r\n        <r:if_content part=\"session\">\r\n          <div class=\"button\">\r\n      	    <a href=\"<r:url />\"><r:title /></a>\r\n      	    <div class=\"submenu\">\r\n              <r:children:each offset=\"1\">\r\n                <div class=\"sm\"><a href=\"<r:url />\"><r:title /></a></div>\r\n              </r:children:each>\r\n      	    </div>\r\n\r\n            <r:children:each offset=\"1\">\r\n              <r:if_children>\r\n                <div class=\"subsubmenu\" title=\"<r:title />\">\r\n                  <r:children:each offset=\"1\">\r\n                    <r:if_self>\r\n                      <div class=\"sm selected\"><a href=\"<r:url />\"><r:title /></a></div>\r\n                    </r:if_self>\r\n                    <r:unless_self>\r\n                      <div class=\"sm\"><a href=\"<r:url />\"><r:title /></a></div>\r\n                    </r:unless_self>\r\n                  </r:children:each>\r\n                </div>\r\n              </r:if_children>\r\n            </r:children:each>\r\n          </div>\r\n        </r:if_content>\r\n      </r:children:each>\r\n    </r:find>\r\n  </div>\r\n  <div style=\"clear:both;\"></div>\r\n</div>\r\n<div style=\"clear:both;\"></div>\r\n\r\n<script type=\"text/javascript\">\r\n<!--\r\njQuery(function ($) {\r\n\r\n  function selectSubmenuButton() {\r\n    var parts = \'<r:url />\'.split(\'/\');\r\n    if (parts.length === 3) {\r\n      $(\'.button a[href*=\'+parts[1]+\']:first\').addClass(\'here\');\r\n      return;\r\n    }\r\n    var url = parts[1] + \'/\' + parts[2];\r\n    var smb = $(\'.submenu .sm a[href*=\'+url+\']\');\r\n    smb.parents(\'.submenu .sm\').addClass(\'selected\');\r\n    smb.parents(\'.submenu\').show();\r\n  }\r\n\r\n  function adjustSubsubmenuHeight() {\r\n    $(\'.button\').each( function () {\r\n      $(\'.submenu .sm\', this).each( function(i, n) {\r\n        $(\'.subsubmenu[title=\'+$(this).text()+\']\').css(\'margin-top\', 18*i);\r\n      });\r\n    });\r\n  }\r\n\r\n  var subMenus = $(\"#mainmenu .submenu\");\r\n  var subSubMenus = $(\"#mainmenu .subsubmenu\");\r\n  var mainButtons = $(\"#mainmenu > .button > a\");\r\n\r\n  subMenus.hide();\r\n  subSubMenus.hide();\r\n  selectSubmenuButton();\r\n  adjustSubsubmenuHeight();\r\n  $(\'.subsubmenu .sm.selected\').parents(\'.subsubmenu\').show();\r\n\r\n  mainButtons.mouseover( function () {\r\n    subMenus.hide();\r\n    subSubMenus.hide();\r\n    $(this).next(\'.submenu\').show();\r\n  });\r\n\r\n  $(\'.submenu .sm\').mouseover( function () {\r\n    subSubMenus.hide();\r\n    $(\'.subsubmenu[title=\'+$(this).text()+\']\').show();\r\n  });\r\n\r\n});\r\n//-->\r\n</script>\r\n\r\n','2009-05-23 01:07:15','2009-06-18 07:39:11',1,1,85),(2,'footer','','<div id=\"footer\">\r\n<div id=\"footermenu\">\r\n<div id=\"footertxt\">\r\n  <a href=\"/\">Home</a> | \r\n  <a href=\"/profile/biography/\">Profile</a> | \r\n  <a href=\"/services/services-offered/site-assessments-workshops/\">Services</a> |